```typescript
function getIntersectionNode(headA: ListNode | null, headB: ListNode | null): ListNode | null {

    if (!headA || !headB) {
        return null;
    }

    let len_a: number = 0;
    let len_b: number = 0;
    let point_a: ListNode | null = headA;
    let point_b: ListNode | null = headB;
    let cutLen: number = 0;

    while (point_a) {
        len_a++;
        point_a = point_a.next;
    }
    while (point_b) {
        len_b++;
        point_b = point_b.next;
    }

    point_a = headA;
    point_b = headB;

    cutLen = Math.abs(len_a - len_b);
    if (cutLen > 0) {
        if (len_a > len_b) {
            while (cutLen > 0) {
                point_a = point_a!.next;
                cutLen--;
            }
        }

        if (len_a < len_b) {
            while (cutLen > 0) {
                point_b = point_b!.next;
                cutLen--;
            }
        }
    }

    for (let index = 0; index < Math.min(len_a, len_b); index++) {
        if (point_a !== point_b) {
            point_a = point_a!.next;
            point_b = point_b!.next;
        } else {
            return point_a;
        }
    }
    return null;
};
