function detectCycle(head: ListNode | null): ListNode | null {

    let cycleMap = new Map();
    let point: ListNode | null = head;

    while (point) {
        if (!cycleMap.has(point)) {
            cycleMap.set(point, 1);
        } else {
            return point;
        }
        point = point.next;
    }
    return null;
};
