        stack_q.push(q);
    }

    while (stack_p.length > 0 && stack_q.length > 0) {
        const top_p: TreeNode = stack_p.pop()!;
        const top_q: TreeNode = stack_q.pop()!;
        if (top_p.val !== top_q.val) {
            return false;
        }
        if (top_p.right && top_q.right) {
            stack_p.push(top_p.right);
            stack_q.push(top_q.right);
        } else if ((!top_p.right && top_q.right)
            || (top_p.right && !top_q.right)) {
            return false;
        }
        if (top_p.left && top_q.left) {
            stack_p.push(top_p.left);
            stack_q.push(top_q.left);
        } else if ((!top_p.left && top_q.left)
            || (top_p.left && !top_q.left)) {
            return false;
        }
    }

    if (stack_p.length === 0 && stack_q.length == 0) {
        return true;
    } else {
        return false;
    }

};
