function serialize(root: TreeNode | null): string {

    let queue: any[] = [];
    let result: any[] = [];

    if (root) {
        queue.push(root);
    }

    while (queue.length) {

        const current_level: any[] = queue;
        queue = [];

        for (let index = 0; index < current_level.length; index++) {
            const element: TreeNode | null = current_level[index];
            if (element === null) {
                result.push(null);
                queue.push(null);
                queue.push(null);
                continue;
            } else {
                result.push(element.val);
                queue.push(element.left);
                queue.push(element.right);
            }
        }

        if (queue.every(ele => ele === null)) {
            break;
        }

    }

    return result.join(',');
};

/*
 * Decodes your encoded data to tree.
 */
function deserialize(data: string): TreeNode | null {

    let arr = data.split(',');
    let rootNode: TreeNode;

    if (arr.length === 0) {
        return null;
    }

    //let level: number = Math.log2(arr.length + 1);
    rootNode = new TreeNode(Number(arr[0]));
    deserialize_sub(rootNode, arr, 0);

};

function deserialize_sub(node: TreeNode, arr: any[], index: number) {
    let left_index = index * 2 + 1;
    let right_index = index * 2 + 2;
    if (arr[left_index]) {
        let left_node: TreeNode = new TreeNode(arr[left_index]);
        node.left = left_node;
        deserialize_sub(left_node, arr, left_index);
    } else {
        node.left = null;
    }
    if (arr[right_index]) {
        let right_node: TreeNode = new TreeNode(arr[right_index]);
        node.right = right_node;
        deserialize_sub(right_node, arr, right_index);
    } else {
        node.right = null;
    }
}

