function numberOfRounds(loginTime: string, logoutTime: string): number {

    let [startHours, startMins] = (loginTime.split(':'));
    let [endHours, endMins] = logoutTime.split(':');

    let startHour: number;
    let startMin: number;
    let endHour: number;
    let endMin: number;

    startMin = parseInt(startMins);
    startHour = parseInt(startHours);
    endHour = parseInt(endHours);
    endMin = parseInt(endMins);

    if (endHour === startHour  && endMin - startMin < 15 && endMin - startMin > 0)
        return 0;

    if (startMin > 0 && startMin <= 15) {
        startMin = 15;
    } else if (startMin > 15 && startMin <= 30) {
        startMin = 30;
    } else if (startMin > 30 && startMin <= 45) {
        startMin = 45;
    } else if (startMin > 45) {
        startMin = 0;
        startHour++;
    }

    if (endMin < 15) {
        endMin = 0;
    } else if (endMin >= 15 && endMin < 30) {
        endMin = 15;
    } else if (endMin >= 30 && endMin < 45) {
        endMin = 30;
    } else if (endMin >= 45) {
        endMin = 45;
    }

    if (endHour > startHour) {
        return ((endHour - startHour) * 60 + (endMin - startMin)) / 15;
    } else if (endHour === startHour) {
        if (startMin > endMin) {
            return ((24 - startHour + endHour) * 60 + (endMin - startMin)) / 15;
        } else {
            return (endMin - startMin) / 15;
        }
    } else {
        return ((24 - startHour + endHour) * 60 + (endMin - startMin)) / 15;
    }
};
