class MyQueue {
    Stack<Integer> st;
    Stack<Integer> ans;
    int f;

    public MyQueue() {
        st = new Stack<>();
        ans = new Stack<>();
    }
    
    public void push(int x) {
        if(st.empty()){
            f =x;
        }
        
        st.push(x);
    }
    
    public int pop() {
        if(ans.empty()){
            while(!st.empty()){
                ans.push(st.pop());
            }
        }
        return ans.pop();
    }
    
    public int peek() {
        if(!ans.empty()) return ans.peek();
        
        return f;
    }
    
    public boolean empty() {

        
        return ans.empty() && st.empty();
    }
    
}
