public class Solution {
    public ListNode detectCycle(ListNode head) {
        Set<ListNode> ans = new HashSet<>();
        if(head == null) return null;
        while(head.next!=null){
            if(ans.contains(head)){
                return head;
            }else{
                ans.add(head);
                head = head.next;
            }
        }
        
        return null;
    }
}

