public int findBottomLeftValue(TreeNode root) {

    int ans = 0;
	
    Queue<TreeNode> q = new LinkedList();
    q.add(root);
    while(!q.isEmpty()){
        int count = q.size();
        for(int i = 0; i<count; i++){
            TreeNode curr = q.poll();
            if(i==0)
                ans = curr.val;
            if(curr.left!=null)
