func getIntersectionNode(headA, headB *ListNode) *ListNode {
    var aHead, bHead = headA, headB
    for aHead != nil && bHead != nil {
        if aHead == bHead {return aHead}
        aHead = aHead.Next
        bHead = bHead.Next

        if aHead == nil && bHead != nil {
            aHead = headB
        } else if bHead == nil && aHead != nil {
            bHead = headA
        }
    }
    return nil
}

