/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if(head == null || k == 0) return head;
        int l = 1 ;
        ListNode dummyHead = head;
        while(head.next != null) {
            head = head.next;
            l++;
        }
        ListNode tail = head;
        k = k % l;
        if(k == 0) return dummyHead;
        l -= k;

        head = dummyHead;
        while(--l > 0) {
            head = head.next;
        }

        ListNode newHead = head.next;
        head.next = null;
        tail.next = dummyHead;
        return newHead;
    }
}

/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public ListNode swapPairs(ListNode head) {
        ListNode dummyHead = new ListNode(-1, head);
        ListNode cur = dummyHead;
        while(cur.next != null && cur.next.next != null) {
            ListNode next2 = cur.next.next;
            ListNode next3 = cur.next.next.next;
            cur.next.next = next3;
            next2.next = cur.next;
            cur.next = next2;
            cur = next2.next; // cur 指向两个要交换节点的前一个节点
        }
        return dummyHead.next;
    }
}

class Solution {
    public TreeNode sortedListToBST(ListNode head) {
        if(head == null ) return null;
        if(head.next == null) return new TreeNode(head.val);
        ListNode pre = null, slow = head, fast = head;
        while(fast!= null && fast.next != null) {
            pre = slow;
            slow = slow.next;
            fast = fast.next.next;
        }
        TreeNode root = new TreeNode(slow.val);
        pre.next = null;
        root.left = sortedListToBST(head);
        root.right = sortedListToBST(slow.next);
        return root;

    }
}

/**
 * Definition for singly-linked list.
 * class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) {
 *         val = x;
 *         next = null;
 *     }
 * }
 */
public class Solution {
    public ListNode detectCycle(ListNode head) {
        ListNode fast = head, slow = head;
        while(fast != null && fast.next !=null) {
            fast = fast.next.next;
            slow = slow.next;
            if(fast == slow) break;
        }
        if(fast == null || fast.next == null) return null;
        // 相遇时 f = 2s  f = s + nb   s=nb  入口点为a + nb 因此slow 走a步到达入口点  两个指针再次一起走就行
        fast = head;
        while(fast != slow) {
            fast =fast.next;
            slow = slow.next;
        }
        return slow;
    }
}

class LRUCache {
    // get put O1  -> hashMap
    // LRU  -> list
    Map<Integer, DoubleEndListNode> map; 
    DoubleEndList list;
    int capacity;
    public LRUCache(int capacity) {
        map = new HashMap<>();
        list = new DoubleEndList();
        this.capacity = capacity;
    }

    public int get(int key) {
       DoubleEndListNode node = map.get(key);
       if(node == null) return -1;
       int val = node.val;
       list.move2Head(node);
       return val;
    }

    public void put(int key, int value) {
        DoubleEndListNode node = map.get(key);
        if(node != null) {
            node.val = value;
            list.move2Head(node);
            return;
        }
        if(map.size() == capacity) {
            // remove tail
            DoubleEndListNode remove = list.removeLast();
            if(remove != null) {
                map.remove(remove.key);
            }
        }
        DoubleEndListNode newNode = new DoubleEndListNode(key, value);
        list.addHead(newNode);
        map.put(key, newNode);
    }

    // 双端链表用于将某个Node前后连接起来 
    private class DoubleEndListNode{
        DoubleEndListNode prev;
        DoubleEndListNode next;
        int val;
        int key;

        DoubleEndListNode() {}
        DoubleEndListNode(int key, int val) {this.key = key; this.val = val;}
    }

    private class DoubleEndList{
        DoubleEndListNode head;
        DoubleEndListNode tail;
        DoubleEndList() {
            head = new DoubleEndListNode();
            tail = new DoubleEndListNode();
            head.next = tail;
            tail.prev = head;
        }

        public void move2Head(DoubleEndListNode node) {
            node.prev.next = node.next;
            node.next.prev = node.prev;
            addHead(node);
        }

        public DoubleEndListNode removeLast() {
            DoubleEndListNode remove = tail.prev;
            DoubleEndListNode last = tail.prev.prev;
            if(last == null) return null;
            last.next = tail;
            tail.prev = last;
            return remove;
        }

        public void addHead(DoubleEndListNode node) {
            node.next = head.next;
            node.prev = head;
            head.next = node;
            node.next.prev = node;
        }
    }
}

