 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int sumNumbers(TreeNode root) {
        return cursion(root, 0);
    }

    public int cursion(TreeNode node, int num) {
        int cur = num*10 + node.val;
        if(node.left == null && node.right == null) return cur;// 叶子节点
        if(node.left == null) return cursion(node.right, cur); // 右分支
        if(node.right == null) return cursion(node.left, cur); // 左分支
        return cursion(node.left, cur) + cursion(node.right, cur); // 二叉
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int findBottomLeftValue(TreeNode root) {
        int[] res = new int[]{-1, 0};
        dfs(root, res, 0);
        return res[1];
    }

    // 二元组  level  val
    public void dfs(TreeNode node, int[] bottomLeft, int h) { // h 水平坐标
        if(h > bottomLeft[0]){
            bottomLeft[0] = h;
            bottomLeft[1] = node.val;
        } 
        if(node.left != null) dfs(node.left, bottomLeft, h + 1);
        if(node.right != null) dfs(node.right, bottomLeft, h + 1);
    }
}

public class Codec {
    private static final String NULL = "NULL";
    private static final String SPLITTER = ",";
    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        if(root == null) return NULL;
        return root.val 
                + SPLITTER 
                + serialize(root.left)
                + SPLITTER 
                + serialize(root.right);
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        Queue<String> queue = new LinkedList<>(Arrays.asList(data.split(SPLITTER)));
        return dfs(queue);
    }

    private TreeNode dfs(Queue<String> queue) {
        String val = queue.poll();
        // Integer 每次赋值或 ++ 引用会被改变 因此不能够 ++ 原因 内部是 final int value;
        if(NULL.equals(val)) {
            return null;
        }
        TreeNode node = new TreeNode(Integer.parseInt(val));
        node.left = dfs(queue);
        node.right = dfs(queue);
        return node;
    }
}

