                int[] item = q.poll();
                tmp.add(item[2]);
                while(!q.isEmpty() && q.peek()[0] == item[0]) { // 同一列
                    tmp.add(q.poll()[2]);
                }
                res.add(tmp);
            }
            return res;
        }

        private void dfs(TreeNode node, Queue<int[]> queue, int x, int y) {
            if(node == null) return;
            queue.add(new int[]{x,y,node.val});
            dfs(node.left, queue, x - 1, y + 1);
            dfs(node.right, queue, x + 1, y + 1);
        }
