class Solution {
    public int searchInsert(int[] nums, int target) {
        int len = nums.length, l = 0, r = len;
        while( l < r) {
            int mid = l + ( (r - l) >> 1 );
            if(nums[mid] < target) {
                l = mid + 1;
            } else {
                r = mid;
            }
        }
        return l;
    }
