public ListNode detectCycle(ListNode head) {
        if (head.next == null || head == null ) {
            return null;
        }
        ListNode fast = head;
        ListNode slow = head;

        while (fast != null && fast.next != null) {
            fast = fast.next.next;
            slow = slow.next;

            if (fast == slow) {
                ListNode node = fast;
                while (node != slow) {
                    node = node.next;
                    slow = node.next;
