class Solution {
public:
    ListNode* h; 
    TreeNode* sortedListToBST(ListNode* head) {
        //if(!head ) return head;
        h = head;
        int n = 0;
        for(auto p = head; p; p = p->next) n ++;
        return build(0, n - 1);

    }

    TreeNode* build(int l, int r) {
        if(l > r) return nullptr;
        TreeNode *root = new TreeNode();
        int mid = (l + r  +1) >> 1;
        root->left = build(l, mid - 1);
        root->val = h->val;   
        h = h->next; 
        root->right = build(mid + 1, r);
        return root;
    }
};
