```c++
class Solution {
public:
    bool judgeCircle(string moves) {
        //想要回到原点，左右上下移动必须同等次数，意味着moves肯定为偶数，若为奇数，必回不到原点。
        if(moves.size()%2!=0){
            return false;
        }
        //下面是偶数情况下的移动
        int coord_x=0;
        int coord_y=0;
        for(auto m:moves){
            if(m=='L') coord_x -= 1;
            if(m=='R') coord_x += 1;
            if(m=='U') coord_y += 1;
            if(m=='D') coord_y -= 1;
        }
        if(coord_x==0 && coord_y==0){
            return true;
        }
        return false;
    }
};
```
