    int t1=60* stoi(logoutTime.substr(0,2)) + stoi(logoutTime.substr(3,5));
    //玩了通宵,t1应再加上一天的时间
    if(t1<t0){
        t1+=1440;
    }
    //当开始时间替换为第一场完整游戏开始的时间
    //t0%15!=0 ,说明第一场完整游戏前必有多余时间
    if(t0%15 !=0) t0=t0+(15-t0%15);
    //结束时间向前取整，舍去最后一场完整游戏结束之后 多余的时间。
    t1=t1/15 * 15;
    return max(0,(t1 - t0)) /15;
    }
};
