```c++
class Solution {
    //具体实现细节
    void merge(vector<int>& nums,int left,int right,vector<int>& ans)
    {
        int mid=(right-left)/2+left;
        int i=left,j=mid+1,k=0;
        //情况1
        while(i<=mid&&j<=right)
        {
            //注意写成 < 就丢失了稳定性（相同元素原来靠前的排序以后依然靠前）
            if(nums[i]<=nums[j]){
                //可以写成ans[k++]=nums[i++]
                ans[k]=nums[i];
                i++;
                k++;
            }
            else {
                ans[k]=nums[j];
                j++;
                k++;
            }
        }
        //情况2
        while(i<=mid)
        {
            ans[k]=nums[i];
            i++;
            k++;
        }
        //情况3
        while(j<=right){
            ans[k]=nums[j];
            j++;
            k++;
        }
        //将临时数组tmp的数组复制到nums中,为什么是i-left?????
        for(int i=left;i<=right;i++)
            nums[i]=ans[i-left];
    }
    //递归
    void mergeSort(vector<int>& nums,int left,int right,vector<int>& ans)
    {
        //递归结束标志
        if(left>=right)
            return ;
        //设置中间值，这样写是为了保证数据过大时不溢出
        int mid=(right-left)/2+left;
        //递归地将数组分为一个个有序序列
        mergeSort(nums,left,mid,ans);
        mergeSort(nums,mid+1,right,ans);
        //再次调用递归将两部分有序数组合二为一
        merge(nums,left,right,ans);
    }
public:
    vector<int> sortArray(vector<int>& nums) {
        vector <int> ans(nums.size());
        mergeSort(nums,0,nums.size()-1,ans);
        return nums;
    }
};
```
