class Solution 
{
public:
    int res;

    void merge(vector<int> & nums, int L, int mid, int R)
    {
        int i = L,  j = mid + 1;
        vector<int> rec;
        while (i <= mid && j <= R)
        {
            if (nums[i] <= nums[j])
                rec.push_back(nums[i++]);
            else
                rec.push_back(nums[j++]);
        }
        ///////////////////////////////////////////
        //借助归并排序的框架    特殊处理
        int ii = L,     jj = mid + 1;
        while (ii <= mid && jj <= R)
        {
            if ((long long)nums[ii] <= 2 * (long long)nums[jj] )
                ii ++;
            else
            {
                res += (mid - ii + 1);
                jj ++ ;
            }
        }
        //////////////////////////////////////////
        while (i <= mid)
            rec.push_back(nums[i ++]);
        while (j <= R)
            rec.push_back(nums[j ++]);
        for (int i=0; i<rec.size(); i++)
            nums[L + i] = rec[i];

    }

    void mergesort(vector<int> & nums, int L, int R)
    {
        if (L < R)
        {
            int mid = L + (R - L) / 2;
            mergesort(nums, L, mid);
            mergesort(nums, mid + 1, R);
            merge(nums, L, mid, R);
        }
    }

    int reversePairs(vector<int>& nums) 
    {
        res = 0;
        mergesort(nums, 0, nums.size() - 1);
        return res;
    }
};

