class CustomStack {
    
    private int[] array;
    private int maxSize;
    private int curSize;

    public CustomStack(int maxSize) {
        this.array = new int[maxSize];
        this.maxSize = maxSize;
        this.curSize = 0;
    }
    
    public void push(int x) {
        if(this.curSize < this.maxSize){
            array[curSize++] = x;
        }
    }
    
    public int pop() {
        if(this.curSize > 0){
            return array[--curSize];
        }
        return -1;
    }
    
    public void increment(int k, int val) {
        if(curSize > 0){
