```java
class Solution {
        public ListNode rotateRight(ListNode head, int k) {
            if (head == null) {
                return head;
            }
            ListNode slow = head;
            ListNode fast = head;
            for (int i = 0; i < k; i++) {
                if (fast.next == null) {
                    //超过一轮时重新开始走,这边也可以优化成模(但是前提需要知道链表总长度至少要遍历一次),可以免去不停的循环遍历
                    fast = head;
                } else {
                    fast = fast.next;
                }
            }
            //快慢指针间距离为0，移动0个位置，直接返回head
            if (fast == head) {
                return head;
            }
            while (fast.next != null) {
                slow = slow.next;
                fast = fast.next;
            }
            ListNode newHead = slow.next;
            fast.next = head;
            slow.next = null;
            return newHead;
        }
    }

