string decodeString(string s) {
  stack<char> str_stack, tmp_stack;
  for(int i=0;s[i];i++){
    if(s[i]!=']') str_stack.push(s[i]);
    else {
      while(str_stack.top()!='['){
        tmp_stack.push(str_stack.top());
        str_stack.pop();
      }
      str_stack.pop();
      string tmp_str="";
      while(!tmp_stack.empty()){
        tmp_str+=tmp_stack.top();
        tmp_stack.pop();
      }
      string single_str="";
      short times=str_stack.top()-'0';
      str_stack.pop();
      string final_str="";
      for(int j=0;j<times;j++){
        final_str+=tmp_str;
      }
      for(int j=0;j<final_str.length();j++){
        str_stack.push(final_str[j]);
      }
    }
  }
  string ret="",ret_reverse="";
  while(!str_stack.empty()){
    ret+=str_stack.top();
    str_stack.pop();
  }
  for(int i=ret.length()-1;i>=0;i--){
    ret_reverse+=ret[i];
  }
  return ret_reverse;
}




