 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    int list_size(ListNode *head){
      int n=0;
      while(head){
        n++;
        head=head->next;
      }
      return n;
    }
    ListNode* rotateRight(ListNode* head, int k) {
      if(!head || !(head->next)) return head;
      k=k%list_size(head);
      ListNode *tail=head->next;
      ListNode *pre_tail=head;
      
      for(int i=0;i<k;i++){
        while(tail->next){
          pre_tail=tail;
          tail=tail->next;
      }
        pre_tail->next=NULL;
        tail->next=head;
        head=tail;
      }
      return head;
    }
};




```

#### **复杂度分析**

- 时间复杂度：O(N^2)，其中 N 为数组长度。
- 空间复杂度：O(N)

