public int[] shortestToChar(String s, char c) {
    int n = s.length();
    int[] ans = new int[n];
    int flag = 0, index_c = 0;
    for(int i = 0; i < n; i++){
        ans[i] = n;//赋初始最大值
        if(s.charAt(i) == c){
            flag = 1;
            index_c = i;
        }
        if(flag == 1){
            ans[i] = i - index_c;//赋距离值
        }
    }
    flag = 0;
    for(int i = n - 1; i >= 0; i--){
        if(s.charAt(i) == c){
            flag = 1;
            index_c = i;
        }
        if(flag == 1){
            if(index_c - i < ans[i])
                ans[i] = index_c - i;//更新距离值
        }
    }
    return ans;
}

class CustomStack {

    private final int maxSize;
    private final int[] stack;
    private final int[] inc;
    private int top = -1;
    private final int bottom = 0;

    public CustomStack(int maxSize) {
        this.maxSize = maxSize;
        stack = new int[maxSize];
        inc = new int[maxSize];
    }

    public void push(int x) {
        if (top == maxSize - 1) {
            return;
        }
        stack[++top] = x;
    }

    public int pop() {
        if (top < bottom) {
            return -1;
        }
        int res = stack[top] + inc[top];
        if (top > bottom) {
            inc[top - 1] += inc[top];
        }
        inc[top] = 0;
        top--;
        return res;
    }

    public void increment(int k, int val) {
        int i = Math.min(k - 1, top);
        if (i >= bottom) {
            inc[i] += val;
        }
    }
}

class MyQueue {
    private Stack<Integer> a;// 
    private Stack<Integer> b;// 

    public MyQueue() {
        a = new Stack<>();
        b = new Stack<>();
    }

    public void push(int x) {
        a.push(x);
    }

    public int pop() {

        if(b.isEmpty()){
            while(!a.isEmpty()){
                b.push(a.pop());
            }
        }
        return b.pop();
    }

    public int peek() {
        if(b.isEmpty()){
            while(!a.isEmpty()){
                b.push(a.pop());
            }
        }
        return b.peek();
    }

