        return a.isEmpty() && b.isEmpty();
    }
}

class Solution {
    public TreeNode sortedListToBST(ListNode head) {
        return build(head, null);
    }
    //[start, end) 
    TreeNode build(ListNode start, ListNode end){
        if(start == end){
            return null;
        }

        ListNode mid = findMid(start, end);
        TreeNode root = new TreeNode(mid.val);
        root.left = build(start, mid);
        root.right = build(mid.next, end);
        return root;
    }
    //two pointers
    ListNode findMid(ListNode start, ListNode end){
        ListNode slow = start, fast = start;
        while (fast != end && fast.next != end){
            slow = slow.next;
            fast = fast.next.next;
        }
        return slow;
    }
}

class Codec:

    def serialize(self, root):
        """Encodes a tree to a single string.

        :type root: TreeNode
        :rtype: str
        """        
        def dfs(root):
            if not root:
                self.s.append('#')
            else:
                self.s.append(str(root.val))
                dfs(root.left)
                dfs(root.right)

        self.s = []  
        dfs(root)
        return ' '.join(self.s)


    def deserialize(self, data):
        """Decodes your encoded data to tree.

        :type data: str
        :rtype: TreeNode
        """
        def dfs():
            if data:
                cur = data.popleft()
                if cur == '#':
                    return
                node = TreeNode(int(cur))
                node.left = dfs()
                node.right = dfs()
            return node

        data = collections.deque(val for val in data.split())
        return dfs()

class Solution {
    Map<Integer, Map<Integer, List<Integer>>> map = new TreeMap<>();
    public List<List<Integer>> verticalTraversal(TreeNode root) {
        tranverse(root, 0, 0);
        List<List<Integer>> results = new ArrayList<>();
        for (int col : map.keySet()) {
            Map<Integer, List<Integer>> curColMap = map.get(col);
            List<Integer> newList = new ArrayList<>();
            for (int row : curColMap.keySet()) {
                Collections.sort(curColMap.get(row));
                newList.addAll(curColMap.get(row));
            }
            results.add(newList);
        }
        return results;
    }
    private void tranverse(TreeNode root, int column, int row) {
        if (root == null) {
            return;
        }
        map.putIfAbsent(column, new TreeMap<>());
        Map<Integer, List<Integer>> curCol = map.get(column);
        curCol.putIfAbsent(row, new ArrayList<>());
        curCol.get(row).add(root.val);
        tranverse(root.left, column - 1, row + 1);
        tranverse(root.right, column + 1, row + 1);
    }
}

