class Solution:
    def lengthOfLongestSubstring(self, s: str) -> int:
        res,left = 0,0
        hash = set()
        for i in range(len(s)):
            if s[i] not in hash:
                hash.add(s[i])
            else:
                res = max(len(hash),res)
                while True:
                    hash.remove(s[left])
                    left += 1
                    if s[i] not in hash:
                        hash.add(s[i])
                        break
        return max(res,len(hash))
