` ListNode* rotateRight(ListNode* head, int k) 
    {
        if(head==nullptr || head->next==nullptr || k==0) return head;
        ListNode* p=head;
        int count=0;
        while(p!=nullptr)
        {
            count++;
            p=p->next;
        }
        k=k%count;
        
        ListNode* fast=head;
        ListNode* slow=head;
        
        for(int i=0;i<k;i++)
