ListNode* swapPairs(ListNode* head) 
    {
        if(head==nullptr || head->next ==nullptr) return head;
        ListNode* vh=new ListNode();
        vh->next=head;
        ListNode* pre=vh;
        ListNode* p1=head;
        ListNode* p2=head->next;
        while(p1!=nullptr && p2!=nullptr)
        {
            swap(pre,p1,p2);
            pre=p1;
            p1=p1->next;
            if(p1==nullptr) break;
            p2=p1->next;
        }
        return vh->next;
    }
    void swap(ListNode* pre, ListNode* p1,ListNode* p2)
    {
        p1->next=p2->next;
        p2->next=p1;
        pre->next=p2;
    }
