int findBottomLeftValue(TreeNode* root) 
    {
        if(root==nullptr) return 0;
        
        vector<int> ans;
        queue<TreeNode*> qe;
        qe.push(root);
        
        while(!qe.empty())
        {
            vector<int> row;
            int count=qe.size();
            while(count!=0)
            {
                TreeNode* a=qe.front();
                qe.pop();
                
                row.push_back(a->val);
                
                if(a->left!=nullptr)
                    qe.push(a->left);
                
                if(a->right!=nullptr)
                    qe.push(a->right);
                
                count--;
            }
            ans=row;
        }
        return ans[0];
}
