    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) 
    {
        if(data=="#") return nullptr;
        vector<string> valArr;
        toStrArr(data,valArr);
        
        TreeNode* root=new TreeNode(std::stod(valArr[0]));
        TreeNode* parent=nullptr;
        
        queue<TreeNode*> qe;
        qe.push(root);
        
        for(int i=1;i<valArr.size();i++)
        {
            parent=qe.front();
            qe.pop();
            
            if(valArr[i].compare("#"))
            {
                parent->left=new TreeNode(std::stod(valArr[i]));
                qe.push(parent->left);
            }
            
            if(valArr[++i].compare("#"))
            {
                parent->right=new TreeNode(std::stod(valArr[i]));
                qe.push(parent->right);
            }
        }
        return root;
    }
