var maxChunksToSorted = function(arr) {
    let res = 0;
    let count = new Map();
    let sortedArr = arr.slice();
    sortedArr.sort((a, b) => a - b);
    for (let i = 0; i < arr.length; i++) {
        if (count.has(arr[i])) {
            if (count.get(arr[i]) == -1) {
                count.delete(arr[i]);
            } else {
                count.set(arr[i], count.get(arr[i]) + 1);
            }
        } else {
            count.set(arr[i], 1);
        }
        if (count.has(sortedArr[i])) {
            if (count.get(sortedArr[i]) == 1) {
                count.delete(sortedArr[i]);
            } else {
                count.set(sortedArr[i],count.get(sortedArr[i]) - 1);
            }
        } else {
            count.set(sortedArr[i], -1);
        }
        if (count.size == 0) {
            res++;
        }
    }
    return res;
};
