var verticalTraversal = function(root) {
    let nodes = new Array();
    function dfs(node, row, col) {
        if (!node) {
            return;
        }
        nodes.push([node.val, row, col]);
        dfs(node.left, row + 1, col - 1);
        dfs(node.right, row + 1, col + 1);
