    dfs(root, 0, 0);
    function sortNodes(a, b) {
        if (a[2] != b[2]) {
            return a[2] - b[2];
        }
        if (a[1] != b[1]) {
            return a[1] - b[1];
        }
        return a[0] - b[0];
    }
    nodes.sort(sortNodes);
    let res = new Array();
    let start = 0;
    for (let i = 1; i < nodes.length; i++) {
        if (nodes[i][2] != nodes[start][2]) {
            let tmp = new Array();
            for (let j = start; j < i; j ++) {
                tmp.push(nodes[j][0]);
            }
            res.push(tmp);
            start = i;
        }
    }
    let tmp = new Array();
