class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if(head == null || k == 0){
            return head;
        }
        int n = calculeLength(head);
        k = k%n;
        
        ListNode fast = head;
        ListNode slow = head;
       
        for(int i = 0; i < k; i++){
            fast = fast.next;
        }
        
        while(fast.next != null){
            fast = fast.next;
            slow = slow.next;
        }
        
        fast.next = head;
        ListNode newHead = slow.next;
        slow.next = null;
        return newHead;
    }
    
    private int calculeLength(ListNode head){
        int count = 0;
        while(head != null){
            head = head.next;
            count++;
        }
        return count;
    }
}
