    TreeNode build(ListNode start, ListNode end){
        if(start == end){
            return null;
        }

        ListNode mid = findMid(start, end);
        TreeNode root = new TreeNode(mid.val);
        root.left = build(start, mid);
        root.right = build(mid.next, end);
        return root;
    }
