public class Codec {
    String SEP = ",";
    String NULL = "#";

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
       StringBuilder res = new StringBuilder();
        serialize(root, res);
        return res.toString();
    }

    void serialize(TreeNode root, StringBuilder res){
        if(root == null){
            res.append(NULL).append(SEP);
            return;
        }
        res.append(root.val).append(SEP);
        serialize(root.left, res);
        serialize(root.right,res);
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        LinkedList<String> nodes = new LinkedList<>();
        for(String s : data.split(SEP)){
            nodes.addLast(s);
        }
        return deserialize(nodes);
    }

    TreeNode deserialize(LinkedList<String> nodes){
        if(nodes.isEmpty()) return null;

        String first = nodes.removeFirst(); //leftmost element is the root 
        if(first.equals(NULL)) return null;//to stop when the path is ended
        TreeNode root = new TreeNode(Integer.parseInt(first));
        root.left = deserialize(nodes);
        root.right = deserialize(nodes);
        return root;
    }  
}
