class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if (head == null) return null;
        ListNode cur = head;
        int count = 1;
        while (cur.next != null){
            cur = cur.next;
            count++;
        }
        cur.next = head;
        for (int i = 1; i <= count - (k % count); i++){
            cur = cur.next;
        }
        ListNode newHead = cur.next;
        cur.next = null; 
        return newHead;              
    }
}
