        stringstream ss(data);
        string var;
        ss >> var;
        TreeNode * root = new TreeNode(stoi(var));
        queue<TreeNode*> que;
        que.push(root);
        while (!que.empty()) {
            int len = que.size();
            for (int i = 0; i < len; i++) {
                TreeNode * node = que.front();
                que.pop();
                string left, right;
                ss>>left>>right;
                if (left!="#") {
