class Solution:
    def maxDepth(self, root: Optional[TreeNode]) -> int:
        '''
        def dfs(node):
            if not node:
                return 0
            return max(dfs(node.left), dfs(node.right)) + 1

        return dfs(root)
        '''
        if not root:
            return 0
        stack = []
        max_depth = 0
        stack.append([root, 1])

        while stack:
            node, depth = stack.pop()
            max_depth = max(max_depth, depth)
            if node.left:
                stack.append((node.left, depth + 1))
            if node.right:
                stack.append((node.right, depth + 1))

        return max_depth
