class Solution {
    public int lengthOfLongestSubstring(String s) {
        // Use Hash Set: the set that only contains distinct elements
        Set<Character> set = new HashSet<>();
        int maxSize = 0;
        int left = 0;

        // Iterate through all characters of string and record the max unique length
        for (int i = 0; i < s.length(); i++) {
            if (!set.add(s.charAt(i))) {
                maxSize = Math.max(maxSize, set.size());
                // Empty the Hashset
                while (s.charAt(left) != s.charAt(i)) {
                    set.remove(s.charAt(left));
                    left++;
                }
                left = left +1;
            }
        } 
        // 上面的算法只有遇到重复项才会更新maxSize，所以若input为全部unique时，maxSize将一直为0
        // 结尾需要再判断一次
        return Math.max(maxSize, set.size());
    }
}
