class Solution {
    public int searchInsert(int[] nums, int target) {
        int l = 0;
        int r = nums.length - 1;
        int mid = (l + r) / 2;

        if (nums.length == 0) return -1;

        if (target > nums[r]) {
            return r + 1;
        } else if (target < nums[l]) {
            return l;
        } else {
            while (l < r) {
                if (target > nums[mid]) {
                    l = mid + 1;
                    mid = (l + r) / 2;
                } else if (target < nums[mid]) {
                    r = mid;
                    mid = (l + r) / 2;
                } else {
                    return mid;
                }
            }
            return mid;
        }
    }
}
