class Solution {
    public int[] sortArray(int[] nums) {
        mergeSort(nums, 0, nums.length-1);
        return nums;
    }

    public void mergeSort (int[] nums, int l, int r) {
        // ❗️这里一定要是 if 而不是 while 要不然 infinite loop！
        if (l < r) {
            int m = l + (r - l) / 2;
            
            mergeSort(nums, l, m);
            mergeSort(nums, m+1, r);

            merge(nums, l, m, r);
        }
    }

    public void merge (int[] nums, int l, int m, int r) {

        // Divide nums into two parts
        int n1 = m - l + 1;
        int n2 = r - m;
        int[] arr1 = new int[n1];
        int[] arr2 = new int[n2];
        for (int i=0; i<n1; i++) {
            arr1[i] = nums[i + l];
        }
        for (int i=0; i<n2; i++) {
            arr2[i] = nums[i + m + 1];
        }

        // Merge two array
        int ls = 0, rs = 0, index = l;
        while (ls < n1 && rs < n2) {
            if (arr1[ls] <= arr2[rs]) {
                nums[index] = arr1[ls];
                ls++;
            } else {
                nums[index] = arr2[rs];
                rs++;
            }             
            index++;     
        }
        while (ls < n1) {
            nums[index] = arr1[ls];
            index++;
            ls++;
        }
        while (rs < n2) {
            nums[index] = arr2[rs];
            index++;
            rs++;
        }        
    }
}
