```cpp
int solve(vector<int>& nums, int k) {
    int n = nums.size();
    if (!n) return -1;
    int target = 0;
    for (int& num : nums) target = (target + num) % k;
    if (target == 0) return target;
    unordered_map<int, int> memo{{0, 0}};
    int prefixSum = 0, res = n;
    for (int i = 0; i < n; i++) {
        prefixSum = (nums[i] + prefixSum) % k;
        int another = (prefixSum - target + k) % k;
        if (memo.count(another)) res = min(res, i - memo[another] + 1);
        memo[prefixSum] = i + 1;
    }
    return res == n ? -1 : res;
}

