class Solution {
public:
    int findJudge(int n, vector<vector<int>>& trust) {
        // edge case
        if (n == 1 && trust.size() == 0)
            return 1; 

        vector<int> num(n+1,0);
        for (auto v : trust) {
            // v[0] trust v[1] so v[0] cannot be the judge
            num.at(v[0]) = -1; 
            num.at(v[1]) += 1;
        }

        for (int i = 0; i < num.size(); i++) {
            if (num.at(i) == n-1) 
                return i;
        }
        return -1;

    }
};

```


