class Solution {
public:
    int firstBadVersion(int n) {
        int l = 1;
        int r = n;
        int m;
        int ret;
        while (l<=r) {
            m = l + (r-l)/2;
            if (isBadVersion(m)) { // m is wrong, but might not be the first wrong one
                r = m - 1;
                ret = m;
            } else {
                l = m + 1;
            }
        }

        return ret;
    }
};

