        out_h, out_min = self.process_time(out_h, out_min, True)

        return max(0, int(((out_h - in_h) * 60 + out_min - in_min)/15))
        

    def process_time(self, hour, minute, out = False):
        
        if not out:
            if minute == 0:
                pass
            elif 0 < minute <= 15:
                minute = 15
            elif 15<minute<=30:
                minute = 30
            elif 30<minute <= 45:
                minute = 45
            else:
                minute = 0
                hour += 1
        else:
            if 0 <= minute < 15:
                minute = 0
            elif 15<=minute<30:
                minute = 15
            elif 30<=minute < 45:
                minute = 30
            else:
                minute = 45
  
        return hour, minute
    
