 *
 * [989] Add to Array-Form of Integer
 */

// @lc code=start
class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        int i = num.length - 1;
        int carry = 0;
        ArrayList<Integer> res = new ArrayList<>();
        while (i>=0 || k != 0) {
            int sum = carry;
            if (i >= 0) {
                sum += num[i];
                i--;
            }
            if (k != 0) {
                sum += k % 10;
                k /= 10;
            }

            carry = sum / 10;
            res.add(sum % 10);
        }

        if (carry > 0) {
            res.add(carry);
        }

        Collections.reverse(res);
        return res;
    }
}
// @lc code=end
