```c++
class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> res;
        int add = 0;
        for (int i = num.size() - 1; i>=0; i--) {
            int digit = k % 10;
            k = k / 10;
            int tmp = num[i] + digit + add;
            if (tmp > 9) {
                res.push_back(tmp - 10);
                add = 1;
            } else {
                res.push_back(tmp);
                add = 0;
            }
        }
        while (k > 0) {
            int tmp = k % 10 + add;
            if (tmp > 9) {
                res.push_back(tmp - 10);
                add = 1;
            } else {
                res.push_back(tmp);
                add = 0;
            }
            k = k / 10;
        }
        if (add == 1) {
            res.push_back(1);
        }
        reverse(res.begin(), res.end());
        return res;
    }
};
```

