class Solution {
public:
    vector<int> addToArrayForm(vector<int>& a, int k) {
        vector<int>res;
        vector<int>b;
        while(k)
        {
            b.push_back(k%10);
            k/=10;
        }
        reverse(b.begin(),b.end());
        int fl=0;
        int i,j;
        for(i=a.size()-1,j=b.size()-1;i>=0&&j>=0;i--,j--)
        {
            int te=a[i]+b[j]+fl;
            fl=te/10;
            res.push_back(te%10);
        }
        while(i>=0)
        {
            int te=a[i]+fl;
            fl=te/10;
            res.push_back(te%10);
            i--;
        }
        while(j>=0)
        {
            int te=b[j]+fl;
            fl=te/10;
            res.push_back(te%10);
            j--;
        }
        if(fl)
            res.push_back(fl);
        reverse(res.begin(),res.end());
        return res;
    }
};
```
