class Solution {
    public List<Integer> addToArrayForm(int[] num, int k){
        //å½“å‰ä½ = (A çš„å½“å‰ä½ + B çš„å½“å‰ä½ + è¿›ä½carry) % 10
        int n = num.length;
        List<Integer> res = new ArrayList<>();
        int carry =0, sum =0,index = n-1;
        while(index >=0 || k!=0){
            // numçš„å½“å‰ä½
            int x = index >=0 ? num[index] : 0;
            // Kçš„å½“å‰ä½
            int y = k != 0 ? k%10 : 0;

            sum = x + y + carry;
            carry = sum / 10;
            k = k / 10;
            index --;
            res.add(sum % 10);
        }
        if (carry != 0){
